import { SET_SHOUTS, LOADING_DATA, LIKE_SHOUT, UNLIKE_SHOUT, STOP_LOADING_UI } from "../types";
import axios from "axios";

// Get all shouts
export const getShouts = () => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get("/shouts")
    .then((res) => {
      dispatch({
        type: SET_SHOUTS,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_SHOUTS,
        payload: [],
      });
    });
};

// Get shout
export const getShout = (shoutId) => (dispatch) => {
    dispatch({ type: LOADING_UI });
    axios
      .get(`/shout/${shoutId}`)
      .then((res) => {
        dispatch({
          type: SET_SHOUT,
          payload: res.data,
        });
        dispatch({ type: STOP_LOADING_UI})
      })
      .catch((err) => console.log(err));
}

// Like a shout
export const likeShout = () => (dispatch) => {
  axios
    .get(`/shout/${shoutId}/like`)
    .then((res) => {
      dispatch({
        type: LIKE_SHOUT,
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};

// Unlike a shout
export const unlikeShout = () => (dispatch) => {
  axios
    .get(`/shout/${shoutId}/unlike`)
    .then((res) => {
      dispatch({
        type: UNLIKE_SHOUT,
        payload: res.data,
      });
    })
    .catch((err) => console.log(err));
};
