import { SET_SHOUTS, LOADING_DATA, LIKE_SHOUT, UNLIKE_SHOUT } from '../types';
import axios from 'axios'

// Get all shouts
export const getShouts = () => (dispatch) => {
    dispatch({ type: LOADING_DATA });
    axios.get('/shouts')
    .then(res=> {
        dispatch({
            type: SET_SHOUTS,
            payload: res.data
        })
    })
    .catch(err => {
        dispatch({
            type: SET_SHOUTS,
            payload: []
        })
    })
}

// Like a shout

