import { SET_ERRORS, CLEAR_ERRORS, LOADING, LOADING_UI } from '../types'

const initialState = {
    loading: false,
    errors: null
}

export default function(state = initialState, action) {
    switch(action.types) {
        case SET_ERRORS:
            return {
                ...state,
                loading: false,
                errors: action.payload
            };
        case CLEAR_ERRORS:
            return {
                ...state,
                loading: false,
                errors: null
            }
        case LOADING_UI:
            return {
                ...state,
                loading: true,

            }
        default:
            return state;
    }
}
